import React from "react";

const [sortedField, setSortedField] = React.useState(null);

const sortTable = (param) => {
  setSortedField(param);
  console.log(param);
  let sortedUsers = [...data];
  console.log(sortedUsers);

  sortedUsers.sort((a, b) => {
    if (a[sortedField] < b[sortedField]) {
      return -1;
    }
    if (a[sortedField] > b[sortedField]) {
      return 1;
    }
    return 0;
  });

  setData(sortedUsers);
};

const Table = () => {
  return (
    <div>
      <table>
        <thead className="padding">
          <tr>
            <th onClick={() => sortTable("fullName")}>Full Name</th>
            <th onClick={() => sortTable("balance")}>Balance</th>
            <th onClick={() => sortTable("isActive")}>Active</th>
            <th onClick={() => sortTable("registered")}>Registered Date</th>
            <th onClick={() => sortTable("state")}>State</th>
            <th onClick={() => sortTable("country")}>Country</th>
          </tr>
        </thead>
        <tbody>
          {data.slice(-10).map((user) => (
            <tr key={user.id + user.registered}>
              <td>{user.fullName}</td>
              <td>{user.balance}</td>
              <td>
                {user.isActive ? (
                  <RiCheckboxCircleLine
                    style={{
                      color: "#3dda33",
                      backgroundColor: "transparent",
                      paddingTop: "5px",
                    }}
                  />
                ) : (
                  <RiCheckboxBlankCircleLine
                    style={{
                      color: "#dc3545",
                      backgroundColor: "transparent",
                      paddingTop: "5px",
                    }}
                  />
                )}
              </td>
              <td>{moment(user.registered).format("LLL")}</td>
              <td>{user.state}</td>
              <td>{user.country}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Table;
